import cardconnect # noqa
from cardconnect.test.helper import (
    CardConnectTest
)

TEST_SIGCAP = {
    'merchid': '496160873888',
    'retref': '110776233356',
    'signature': '''''' # noqa
}


class SigCapTest(CardConnectTest): # noqa

    def test_sigcap_create(self): # noqa
        cardconnect.SigCap.create(**TEST_SIGCAP)

        self.requestor_mock.request.assert_called_with(
            'put',
            '/sigcap',
            TEST_SIGCAP
        )
